* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }
  
  :root {
    --app-wrapper-width: 1000px;
    --app-wrapper-height: 600px;
    --app-wrapper-padding: 20px 40px;
    --app-color-icons-text: #5f5f5f;
  }
  
  html {
    font-size: 10px;
  }
  
  body {
    position: relative;
    width: 100%;
    min-height: 100vh;
    font-size: 1.6rem;
    font-family: "Roboto", sans-serif;
    background-color: #575757;
  }
  
  .bgiContainer {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    content: "";
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center center;
    filter: blur(2px);
    -webkit-filter: blur(2px);
    z-index: -1;
  }
  
  .bgiContainer.thunderstorm-day {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Thunderstorm-day.jpg?alt=media&token=206107f9-9a19-4173-8759-7b704aa96fc4");
  }
  
  .bgiContainer.thunderstorm-night {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Thunderstorm-night.jpg?alt=media&token=9fcd179b-31a4-48eb-93d5-bc7f6058cfde");
  }
  
  .bgiContainer.rain-day {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Rain-day.jpg?alt=media&token=bd119570-5618-4817-84a4-80615dc834ef");
  }
  
  .bgiContainer.rain-night {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Rain-night-1.jpg?alt=media&token=cdfd7122-a210-4286-8297-f4704afd88a2");
  }
  
  .bgiContainer.snow-day {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Snow-day.jpg?alt=media&token=442f207f-ed45-47c2-a8a9-e217a9c19f21");
  }
  
  .bgiContainer.snow-night {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Snow-night.jpg?alt=media&token=064812b3-2975-4001-bce8-dca50d2f42ca");
  }
  
  .bgiContainer.fog-day {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Fog-day.jpg?alt=media&token=201d3d94-5615-4a71-a191-94e7091b4eab");
  }
  
  .bgiContainer.fog-night {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Fog-nigth.jpg?alt=media&token=10958719-f5ee-4f2b-b1d1-6c02c8a85fb1");
  }
  
  .bgiContainer.squall-day-night {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Squall-day-night.jpg?alt=media&token=4ffc0d9d-96f5-429f-95d7-6a641cdef4f6");
  }
  
  .bgiContainer.tornado {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Tornado.jpg?alt=media&token=f78abe9e-bfd4-4af5-919c-5fc0fb516260");
  }
  
  .bgiContainer.clear-sky-day {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Clear-day-1.jpg?alt=media&token=8834f4e8-a226-4173-b5b2-b890025976c5");
  }
  
  .bgiContainer.clear-sky-night {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Clear-night.jpg?alt=media&token=197080c1-f461-45b1-9b4b-d68b20967418");
  }
  
  .bgiContainer.clouds-day {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Clouds-day.jpg?alt=media&token=81afcae1-f9a5-414e-8c3f-c4244da3e1fb");
  }
  
  .bgiContainer.clouds-night {
    background-image: url("https://firebasestorage.googleapis.com/v0/b/weather-app-79d32.appspot.com/o/Clouds-night.jpg?alt=media&token=17cbae71-3f03-490f-aa63-2e08f720cffc");
  }
  
  .bgiContainer.time-day {
    background: #56ccf2;
    background: -webkit-linear-gradient(to right, #2f80ed, #56ccf2);
    background: linear-gradient(to right, #2f80ed, #56ccf2);
  }
  
  .bgiContainer.time-night {
    background: #0f2027;
    background: -webkit-linear-gradient(to right, #2c5364, #203a43, #0f2027);
    background: linear-gradient(to right, #2c5364, #203a43, #0f2027);
  }
  
  .main {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    overflow: hidden;
  }
  
  .app-wrapper {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 10px;
  }
  
  .input-message {
    color: rgba(255, 255, 255, 0.82);
    font-size: 1.3rem;
    font-weight: 300;
  }
  
  .input-message.clear-sky-day {
    color: white;
  }
  
  .input-message.clear-sky-night,
  .input-message.clouds-day,
  .input-message.clouds-night,
  .input-message.rain-night,
  .input-message.snow-day,
  .input-message.snow-night,
  .input-message.fog-day,
  .input-message.fog-night,
  .input-message.thunderstorm-day,
  .input-message.thunderstorm-night,
  .input-message.tornado,
  .input-message.squall-day-night {
    color: rgba(255, 255, 255, 0.82);
  }
  
  .input-message.rain-day {
    color: rgba(87, 87, 87, 1);
  }
  
  .input-message--success {
    color: #2ecc40;
    font-size: 1.3rem;
  }
  
  .search-bar {
    display: flex;
    align-items: center;
    width: 100%;
    margin-bottom: 10px;
  }
  
  .search-bar__wrapper {
    width: 100%;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
  }
  
  .form {
    display: flex;
    width: 100%;
  }
  
  .search-bar__input {
    width: 33.3%;
    padding: 8px 0;
    margin-bottom: 5px;
    border: none;
    border-bottom-color: transparent;
    background-color: transparent;
    font-size: 1.1rem;
    outline: none;
    font-family: "Roboto", sans-serif;
    font-weight: 300;
    color: rgba(255, 255, 255, 0.82);
    caret-color: rgba(255, 255, 255, 0.82);
  }
  
  .search-bar__input--city.clear-sky-day,
  .search-bar__input--country.clear-sky-day {
    border-bottom: 1px solid #49d7ffb3;
    color: white;
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__input--city.clear-sky-day::placeholder,
  .search-bar__input--country.clear-sky-day::placeholder,
  .search-bar__input--city::placeholder,
  .search-bar__input--country::placeholder {
    color: white;
    font-weight: 100;
  }
  
  .search-bar__input--city.clear-sky-day:-ms-input-placeholder,
  .search-bar__input--country.clear-sky-day:-ms-input-placeholder,
  .search-bar__input--city:-ms-input-placeholder,
  .search-bar__input--country:-ms-input-placeholder {
    color: white;
    font-weight: 100;
  }
  
  .search-bar__input--city.clear-sky-day::-ms-input-placeholder,
  .search-bar__input--country.clear-sky-day::-ms-input-placeholder,
  .search-bar__input--city::-ms-input-placeholder,
  .search-bar__input--country::-ms-input-placeholder {
    color: white;
    font-weight: 100;
  }
  
  .search-bar__input--city.fog-day::placeholder,
  .search-bar__input--country.fog-day::placeholder {
    color: rgba(255, 255, 255, 1);
    font-weight: 100;
  }
  
  .search-bar__input--city.fog-day:-ms-input-placeholder,
  .search-bar__input--country.fog-day:-ms-input-placeholder {
    color: rgba(255, 255, 255, 1);
    font-weight: 100;
  }
  
  .search-bar__input--city.fog-day::-ms-input-placeholder,
  .search-bar__input--country.fog-day::-ms-input-placeholder {
    color: rgba(255, 255, 255, 1);
    font-weight: 100;
  }
  
  .search-bar__input--city.clear-sky-night::placeholder,
  .search-bar__input--country.clear-sky-night::placeholder,
  .search-bar__input--city.clouds-day::placeholder,
  .search-bar__input--country.clouds-day::placeholder,
  .search-bar__input--city.clouds-night::placeholder,
  .search-bar__input--country.clouds-night::placeholder,
  .search-bar__input--city.rain-night::placeholder,
  .search-bar__input--country.rain-night::placeholder,
  .search-bar__input--city.snow-day::placeholder,
  .search-bar__input--country.snow-day::placeholder,
  .search-bar__input--city.snow-night::placeholder,
  .search-bar__input--country.snow-night::placeholder,
  .search-bar__input--city.fog-night::placeholder,
  .search-bar__input--country.fog-night::placeholder,
  .search-bar__input--city.thunderstorm-day::placeholder,
  .search-bar__input--country.thunderstorm-day::placeholder,
  .search-bar__input--city.thunderstorm-night::placeholder,
  .search-bar__input--country.thunderstorm-night::placeholder,
  .search-bar__input--city.tornado::placeholder,
  .search-bar__input--country.tornado::placeholder,
  .search-bar__input--city.squall-day-night::placeholder,
  .search-bar__input--country.squall-day-night::placeholder {
    color: rgba(255, 255, 255, 0.82);
    font-weight: 100;
  }
  
  .search-bar__input--city.clear-sky-night:-ms-input-placeholder,
  .search-bar__input--country.clear-sky-night:-ms-input-placeholder,
  .search-bar__input--city.clouds-day:-ms-input-placeholder,
  .search-bar__input--country.clouds-day:-ms-input-placeholder,
  .search-bar__input--city.clouds-night:-ms-input-placeholder,
  .search-bar__input--country.clouds-night:-ms-input-placeholder,
  .search-bar__input--city.rain-night:-ms-input-placeholder,
  .search-bar__input--country.rain-night:-ms-input-placeholder,
  .search-bar__input--city.snow-day:-ms-input-placeholder,
  .search-bar__input--country.snow-day:-ms-input-placeholder,
  .search-bar__input--city.snow-night:-ms-input-placeholder,
  .search-bar__input--country.snow-night:-ms-input-placeholder,
  .search-bar__input--city.fog-night:-ms-input-placeholder,
  .search-bar__input--country.fog-night:-ms-input-placeholder,
  .search-bar__input--city.thunderstorm-day:-ms-input-placeholder,
  .search-bar__input--country.thunderstorm-day:-ms-input-placeholder,
  .search-bar__input--city.thunderstorm-night:-ms-input-placeholder,
  .search-bar__input--country.thunderstorm-night:-ms-input-placeholder,
  .search-bar__input--city.tornado:-ms-input-placeholder,
  .search-bar__input--country.tornado:-ms-input-placeholder,
  .search-bar__input--city.squall-day-night:-ms-input-placeholder,
  .search-bar__input--country.squall-day-night:-ms-input-placeholder {
    color: rgba(255, 255, 255, 0.82);
    font-weight: 100;
  }
  
  .search-bar__input--city.clear-sky-night::-ms-input-placeholder,
  .search-bar__input--country.clear-sky-night::-ms-input-placeholder,
  .search-bar__input--city.clouds-day::-ms-input-placeholder,
  .search-bar__input--country.clouds-day::-ms-input-placeholder,
  .search-bar__input--city.clouds-night::-ms-input-placeholder,
  .search-bar__input--country.clouds-night::-ms-input-placeholder,
  .search-bar__input--city.rain-night::-ms-input-placeholder,
  .search-bar__input--country.rain-night::-ms-input-placeholder,
  .search-bar__input--city.snow-day::-ms-input-placeholder,
  .search-bar__input--country.snow-day::-ms-input-placeholder,
  .search-bar__input--city.snow-night::-ms-input-placeholder,
  .search-bar__input--country.snow-night::-ms-input-placeholder,
  .search-bar__input--city.fog-night::-ms-input-placeholder,
  .search-bar__input--country.fog-night::-ms-input-placeholder,
  .search-bar__input--city.thunderstorm-day::-ms-input-placeholder,
  .search-bar__input--country.thunderstorm-day::-ms-input-placeholder,
  .search-bar__input--city.thunderstorm-night::-ms-input-placeholder,
  .search-bar__input--country.thunderstorm-night::-ms-input-placeholder,
  .search-bar__input--city.tornado::-ms-input-placeholder,
  .search-bar__input--country.tornado::-ms-input-placeholder,
  .search-bar__input--city.squall-day-night::-ms-input-placeholder,
  .search-bar__input--country.squall-day-night:-ms-input-placeholder {
    color: rgba(255, 255, 255, 0.82);
    font-weight: 100;
  }
  
  .search-bar__input--city.rain-day::placeholder,
  .search-bar__input--country.rain-day::placeholder {
    color: rgb(0, 0, 0);
    font-weight: 300;
  }
  
  .search-bar__input--city.rain-day:-ms-input-placeholder,
  .search-bar__input--country.rain-day:-ms-input-placeholder {
    color: rgb(0, 0, 0);
    font-weight: 300;
  }
  
  .search-bar__input--city.rain-day::-ms-input-placeholder,
  .search-bar__input--country.rain-day::-ms-input-placeholder {
    color: rgb(0, 0, 0);
    font-weight: 300;
  }
  
  .search-bar__input--city.clear-sky-night,
  .search-bar__input--country.clear-sky-night {
    border-bottom: 1px solid #3498dbc2;
    color: rgba(255, 255, 255, 0.82);
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__input--city.clouds-day,
  .search-bar__input--country.clouds-day,
  .search-bar__input--city.snow-day,
  .search-bar__input--country.snow-day,
  .search-bar__input--city.snow-night,
  .search-bar__input--country.snow-night,
  .search-bar__input--city.fog-night,
  .search-bar__input--country.fog-night {
    border-bottom: 1px solid #4486d8;
    color: rgba(255, 255, 255, 0.82);
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__input--city.clouds-night,
  .search-bar__input--country.clouds-night {
    border-bottom: 1px solid #4286f4b3;
    color: rgba(255, 255, 255, 0.82);
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__input--city.rain-day,
  .search-bar__input--country.rain-day {
    border-bottom: 1px solid #e9e4f082;
    color: rgba(0, 0, 0, 0.82);
    font-weight: 300;
    caret-color: rgb(0 0 0 / 80%);
  }
  
  .search-bar__input--city.rain-night,
  .search-bar__input--country.rain-night {
    border-bottom: 1px solid #cc5333a6;
    color: rgba(255, 255, 255, 1);
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__input--city.fog-day,
  .search-bar__input--country.fog-day {
    border-bottom: 1px solid rgba(238, 242, 243, 0.5);
    color: rgba(255, 255, 255, 1);
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__input--city.thunderstorm-day,
  .search-bar__input--country.thunderstorm-day {
    border-bottom: 1px solid #928dab94;
    color: rgba(255, 255, 255, 1);
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__input--city.thunderstorm-night,
  .search-bar__input--country.thunderstorm-night {
    border-bottom: 1px solid #516395a8;
    color: rgba(255, 255, 255, 1);
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__input--city.tornado,
  .search-bar__input--country.tornado,
  .search-bar__input--city.squall-day-night,
  .search-bar__input--country.squall-day-night {
    border-bottom: 1px solid #525252c7;
    color: rgba(255, 255, 255, 1);
    font-weight: 300;
    caret-color: rgb(255 255 255 / 80%);
  }
  
  .search-bar__button-search {
    width: 33.3%;
    padding: 8px 0;
    margin-bottom: 5px;
    border: none;
    border-top-right-radius: 10px;
    background: transparent;
    color: rgba(255, 255, 255, 0.82);
    outline: none;
    cursor: pointer;
  }
  
  .search-bar__button-search.clear-sky-day {
    background: #2394e2;
    background: -webkit-linear-gradient(to right, #2394e2 -70%, #2394e2);
    background: linear-gradient(to right, #2394e2 -70%, #2394e2);
    color: white;
    font-weight: 300;
  }
  
  .search-bar__button-search.clear-sky-night {
    background: #3498dbc2;
    background: -webkit-linear-gradient(to right, #3498dbc2, #3498dbc2);
    background: linear-gradient(to right, #3498dbc2, #3498dbc2);
    color: rgba(255, 255, 255, 0.82);
    font-weight: 300;
  }
  
  .search-bar__button-search.clouds-day,
  .search-bar__button-search.snow-day,
  .search-bar__button-search.snow-night,
  .search-bar__button-search.fog-night {
    background: #3d72b48c;
    background: -webkit-linear-gradient(to right, #3d72b48c, #3d72b48c);
    background: linear-gradient(to right, #3d72b48c, #3d72b48c);
    color: rgba(255, 255, 255, 0.82);
  }
  
  .search-bar__button-search.clouds-night {
    background: #4286f4b3;
    background: -webkit-linear-gradient(to bottom, #4286f4b3 50%, #4286f4b3);
    color: rgb(255 255 255 / 82%);
    background: linear-gradient(to bottom, #4286f4b3 50%, #4286f4b3);
  }
  
  .search-bar__button-search.rain-day {
    background: #d3cce3a3;
    background: -webkit-linear-gradient(to right, #e9e4f082, #d3cce3a3);
    background: linear-gradient(to right, #e9e4f082, #d3cce3a3);
  }
  
  .search-bar__button-search.rain-night {
    background: #cc5333a6;
    background: -webkit-linear-gradient(to bottom, #cc5333a6, #cc5333a6);
    background: linear-gradient(to bottom, #cc5333a6, #cc5333a6);
    color: rgba(255, 255, 255, 0.82);
  }
  
  .search-bar__button-search.fog-day {
    background: rgba(238, 242, 243, 0.5);
    background: -webkit-linear-gradient(
      to right,
      rgb(238, 242, 243, 0.5),
      rgb(199, 199, 199, 0.5)
    );
    background: linear-gradient(
      to right,
      rgb(238, 242, 243, 0.5),
      rgb(199, 199, 199, 0.5)
    );
  }
  
  .search-bar__button-search.thunderstorm-day {
    background: #928dab94;
    background: -webkit-linear-gradient(to right, #928dab94, #928dab94);
    background: linear-gradient(to bottom, #928dab94, #928dab94);
    color: rgba(255, 255, 255, 0.82);
  }
  
  .search-bar__button-search.thunderstorm-night {
    background: #516395a8;
    background: -webkit-linear-gradient(to bottom, #516395a8, #61438585);
    background: linear-gradient(to bottom, #516395a8, #61438585);
    color: rgba(255, 255, 255, 0.82);
  }
  
  .search-bar__button-search.tornado,
  .search-bar__button-search.squall-day-night {
    background-color: #3d72b48c;
    background: linear-gradient(to right, #525252c7, #525252c7);
    background: linear-gradient(to right, #525252c7, #525252c7);
    color: rgba(255, 255, 255, 0.82);
  }
  
  .search-bar__button-remove {
    width: 100%;
    padding: 8px 0;
    border: none;
    background: transparent;
    color: rgba(255, 255, 255, 0.82);
    outline: none;
    cursor: pointer;
  }
  
  .search-bar__button-remove.clear-sky-day {
    background: #49d7ff78;
    background: -webkit-linear-gradient(to right, #49d7ff78 -70%, #49d7ff78);
    background: linear-gradient(to right, #49d7ff78 -70%, #49d7ff78);
    color: white;
    font-weight: 300;
  }
  
  .search-bar__button-remove.clear-sky-night {
    background: #3498dbc2;
    background: -webkit-linear-gradient(to right, #3498dbc2, #3498dbc2);
    background: linear-gradient(to right, #3498dbc2, #3498dbc2);
    color: rgba(255, 255, 255, 0.82);
    font-weight: 300;
  }
  
  .search-bar__button-remove.clouds-day,
  .search-bar__button-remove.snow-day,
  .search-bar__button-remove.snow-night,
  .search-bar__button-remove.fog-night {
    background: #3d72b48c;
    background: -webkit-linear-gradient(to right, #3d72b48c, #3d72b48c);
    background: linear-gradient(to right, #3d72b48c, #3d72b48c);
    color: rgba(255, 255, 255, 0.82);
  }
  
  .search-bar__button-remove.clouds-night {
    background: #4286f4b3;
    background: -webkit-linear-gradient(to bottom, #4286f4b3 50%, #4286f4b3);
    color: rgb(255 255 255 / 82%);
    background: linear-gradient(to bottom, #4286f4b3 50%, #4286f4b3);
  }
  
  .search-bar__button-remove.rain-night {
    background: #cc5333a6;
    background: -webkit-linear-gradient(to bottom, #cc5333a6, #cc5333a6);
    background: linear-gradient(to bottom, #cc5333a6, #cc5333a6);
    color: rgba(255, 255, 255, 0.82);
  }
  
  .search-bar__button-remove.rain-day {
    background: #d3cce3a3;
    background: -webkit-linear-gradient(to right, #e9e4f082, #d3cce3a3);
    background: linear-gradient(to right, #e9e4f082, #d3cce3a3);
  }
  
  .search-bar__button-remove.fog-day {
    background: rgb(238, 242, 243, 0.5);
    background: -webkit-linear-gradient(to right, #c7c7c780, #c7c7c780);
    background: linear-gradient(to right, #c7c7c780, #c7c7c780);
  }
  
  .search-bar__button-remove.thunderstorm-day {
