.slider {
  position: relative;
  width: 100%;
  height: 89%;
  overflow: hidden;
  border-radius: 10px;
}

.slider__content {
  display: flex;
  justify-content: space-between;
  width: 100%;
  height: 100%;
}

.slider__wrapper-img {
  display: block;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
}

.slider__svg-wrapper {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.slider__svg-wrapper.day {
  background: #56ccf2;
  background: -webkit-linear-gradient(to right, #2f80ed, #56ccf2);
  background: linear-gradient(to right, #2f80ed, #56ccf2);
}

.slider__svg-wrapper.night {
  background: #0f2027;
  background: -webkit-linear-gradient(to right, #2c5364, #203a43, #0f2027);
  background: linear-gradient(to right, #2c5364, #203a43, #0f2027);
}

.svg-loader {
  display: inline-block;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100px;
  height: 100px;
}

.slider__loader-text {
  color: white;
  transform: translateY(80px);
}

.slider__loader-dot {
  animation: dot 1.5s infinite ease;
}

.slider__loader-dot:nth-child(2) {
  animation-delay: 0.5s;
}

.slider__loader-dot:nth-child(3) {
  animation-delay: 1s;
}

@keyframes dot {
  0% {
    opacity: 0.4;
    transform: scale(1);
  }

  50% {
    opacity: 1;
    transform: scale(1.2);
  }

  100% {
    opacity: 0.4;
    transform: scale(1);
  }
}

.slider__svg-wrapper.hide,
.svg-loader.hide {
  display: none;
}

.slider__img-logo {
  max-width: 30%;
  margin-bottom: 10px;
}

.slider__title-logo {
  font-size: 1.8rem;
  color: rgba(255, 255, 255, 0.8);
}

.slider__image-wrapper {
  display: grid;
  grid-template-columns: 100%;
  grid-template-rows: 150px 200px 150px 25px;
  min-width: 100%;
  height: 100%;
  row-gap: 3px;
  grid-template-areas:
    "box1"
    "box2"
    "box4"
    "box5";
}

.slider__inner-box:nth-child(1) {
  display: flex;
  grid-area: box1;
}

